INSERT INTO public.tutorials (title, description, video_url, category, display_order) VALUES
('Como integrar com WhatsApp?', 'Conecte seu número de WhatsApp para que o agente responda automaticamente seus clientes. Basta escanear o QR Code e pronto!', '', 'Integrações', 2),
('Como cadastrar serviços?', 'Adicione seus serviços com nome, preço e duração. Organize por categorias para facilitar o agendamento dos clientes.', '', 'Configurações', 3),
('Como gerenciar a agenda?', 'Configure os horários de funcionamento da sua barbearia e de cada profissional. Defina intervalos de almoço e dias de folga.', '', 'Agenda', 4),
('Como criar um agendamento?', 'Agende clientes manualmente pelo painel. Escolha o serviço, profissional, data e horário disponível.', '', 'Agenda', 5),
('Como cadastrar clientes?', 'Adicione clientes com nome, telefone e outras informações. O histórico de atendimentos fica salvo automaticamente.', '', 'Clientes', 6),
('Como configurar lembretes?', 'Ative lembretes automáticos por WhatsApp para reduzir faltas. Configure quanto tempo antes do horário o cliente será avisado.', '', 'Configurações', 7),
('Como criar planos do clube?', 'Monte planos de assinatura para fidelizar clientes. Defina quais serviços estão incluídos e o valor mensal.', '', 'Clube', 8),
('Como funciona o agente de IA?', 'O agente responde automaticamente no WhatsApp, tira dúvidas sobre serviços, preços e agenda horários para seus clientes.', '', 'Agente', 9),
('Como adicionar profissionais?', 'Convide sua equipe para o sistema. Cada profissional pode ter sua própria agenda e serviços específicos.', '', 'Equipe', 10),
('Como ver relatórios e faturamento?', 'Acompanhe o desempenho da barbearia com estatísticas de agendamentos, faturamento e clientes atendidos.', '', 'Financeiro', 11);